  });

export const PostDownload = async (url: string, body?: any, opts?: Omit<RequestOptions, 'method' | 'url'>) =>
  sendRequest({
    url,
    method: 'PUT',
    contentType: 'application/json',
    responseType: 'blob',
    body,
    ...opts,
  });
